-- ----------------------------
-- View structure for user_annotate
-- ----------------------------
DROP VIEW IF EXISTS `user_annotate`;
CREATE ALGORITHM = UNDEFINED DEFINER = `root`@`localhost` SQL SECURITY DEFINER VIEW `user_annotate` AS select `user`.`account` AS `account`,`annotation`.`content` AS `content`,`annotation`.`selected` AS `selected`,`passage`.`title` AS `title`,`passage`.`id` AS `id`,`user`.`id` AS `userId` from ((`user` join `annotation`) join `passage`);

-- ----------------------------
-- Triggers structure for table annotation
-- ----------------------------
DROP TRIGGER IF EXISTS `deleteLike`;
delimiter ;;
CREATE TRIGGER `deleteLike` AFTER DELETE ON `annotation` FOR EACH ROW begin

delete from dianzan where annotation_id = OLD.id;

end
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table dianzan
-- ----------------------------
DROP TRIGGER IF EXISTS `countLike`;
delimiter ;;
CREATE TRIGGER `countLike` AFTER INSERT ON `dianzan` FOR EACH ROW begin

if(NEW.annotation_id is null and NEW.passage_id in (select pa_id from likecount where type = 0))
then
update likecount set num = num+1 where type = 0 and pa_id = NEW.passage_id;
end if;

if(NEW.annotation_id is not null and NEW.annotation_id in (select pa_id from likecount where type = 1))
then
update likecount set num = num+1 where type = 1 and pa_id = NEW.annotation_id;
end if;

if(NEW.annotation_id is null and NEW.passage_id not in (select pa_id from likecount where type = 0))
then
insert into likecount(type,pa_id,num) value(0,NEW.passage_id,1);
end if;

if(NEW.annotation_id is not null and NEW.annotation_id not in (select pa_id from likecount where type = 1))
then
insert into likecount(type,pa_id,num) value(1,NEW.annotation_id,1);
end if;

end
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table dianzan
-- ----------------------------
DROP TRIGGER IF EXISTS `countLikeDel`;
delimiter ;;
CREATE TRIGGER `countLikeDel` AFTER DELETE ON `dianzan` FOR EACH ROW begin
declare cnum int;
if(OLD.annotation_id is null) then
	select num into cnum from likecount where type = 0 and pa_id = OLD.passage_id;
	if(cnum = 1) then
		delete from likecount where type = 0 and pa_id = OLD.passage_id; 
	else
		update likecount set num = num-1 where type = 0 and pa_id = OLD.passage_id;
	end if;
else
	select num into cnum from likecount where type = 1 and pa_id = OLD.annotation_id;
	if(cnum =1) then
		delete from likecount where type = 1 and pa_id = OLD.annotation_id;
	else
		update likecount set num = num-1 where type = 1 and pa_id = OLD.annotation_id;
	end if;
end if;

end
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table mygroup
-- ----------------------------
DROP TRIGGER IF EXISTS `deleteMem`;
delimiter ;;
CREATE TRIGGER `deleteMem` AFTER DELETE ON `mygroup` FOR EACH ROW begin
      delete from groupmember where group_id=OLD.id;
end
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table passage
-- ----------------------------
DROP TRIGGER IF EXISTS `deleteAnnotation`;
delimiter ;;
CREATE TRIGGER `deleteAnnotation` AFTER DELETE ON `passage` FOR EACH ROW begin
      if(OLD.type=0) then
            delete from homework where passage_id=OLD.id;
      end if;
      delete from annotation where passage_id=OLD.id;
      delete from dianzan where passage_id = OLD.id;
end
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
